from Crypto.Util.number import isPrime, bytes_to_long
import random

FLAG = b"Securinets{REDACTED}"


def getPrime():
    while True:
        a, b, c = random.getrandbits(256), random.getrandbits(
            256),  random.getrandbits(256)
        p = (a << 1024) + (b << 512) + c
        if isPrime(p):
            return p


p = getPrime()
q = getPrime()

n = p * q
e = 0x10001
m = bytes_to_long(FLAG)
c = pow(m, e, n)
print("n = ", n)
print("e = ", e)
print("c = ", c)

